import React, {useState} from "react";
import {BrowserRouter as Router, Link, Route, Switch, useParams} from "react-router-dom";
import {Button, Table, Modal} from 'react-bootstrap';

// const Details = (props) =>{
//
// }
// export default Details;

function Details(props) {

    // We can use the `useParams` hook here to access
    // the dynamic pieces of the URL.
    let {degreeName} = useParams();
    // return (
    //     <div>
    //         {/*<h3>ID: {degreeName}</h3>*/}
    //         <p> {props.singleCourse.degreeType}</p>
    //     </div>
    // );
    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    return (
        <div className="container">
            <h1 onClick={handleShow}>{props.singleCourse.crawledCourses.degreeName}</h1>

            {/*<Button variant="primary" onClick={handleShow}>*/}
            {/*    Open Demo Model*/}
            {/*</Button>*/}

            <Modal size="lg" show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                    {props.singleCourse.crawledCourses.degreeName !== '' ?
                        <Modal.Title>{props.singleCourse.crawledCourses.degreeName}</Modal.Title> :
                        <Modal.Title>else </Modal.Title>}
                </Modal.Header>
                <Modal.Body>
                    <Table>
                        <thead>
                        <th>1 </th>
                        <th>2 </th>
                        </thead>
                        <tbody>
                        <tr>
                            <td>Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod
                                tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam,
                                quis nostrud exercitation ullamco labo
                            </td>
                            <td> ris nisi ut aliquip ex ea commodo
                                consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse
                                cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non
                                proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
                            </td>
                        </tr>
                        </tbody>

                    </Table>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Close
                    </Button>
                    <Button variant="primary" onClick={handleClose}>
                        Save It!
                    </Button>
                </Modal.Footer>
            </Modal>
        </div>
    );

}

export default Details;
